export const addTask = text => ({
    type: 'ADD_TASK',
    text
})

export const deleteTask = id => ({
    type: 'DELETE_TASK',
    id
})

export const editTask = (id, payload) => ({
    type: 'EDIT_TASK',
    id: payload.id,
    text: payload.text
})
