export const Reducer = (state = [], action) => {
    console.log("reducer", action)
    switch (action.type) {
        case 'ADD_TASK':
            console.log('ADD_TASK', state)
            return  ({...state,

                //   id: action.id,
                  text: action.text,
                })
            // ]
        default:
            console.log('   DEFAULT REDUCER', state)
            return state
    }
}
