let newId = 0;
export const Reducer = (state = [], action) => {
    console.log("reducer", action)
    switch (action.type) {
        case 'ADD_TASK':
            return [ ...state,
                {
                  id: newId++,
                  text: action.text,
                }
            ]
        case 'DELETE_TASK':
            return state.filter(task => task.id !== action.id)

        case 'EDIT_TASK':
            console.log('EDIT_TASK', typeof(action.id), action.id)
            return [
                ...state,
                state.map((task) => {
                    console.log(task, "redu", task.id === action.id )
                return task.id == action.id 
                ? 
                    {...task, text: action.text  }
                : task
                })
            ]

        default:
            return state
    }
}
