export const Reducer = (state = {text:[]}, action) => {
    console.log("reducer", action)
    switch (action.type) {
        case 'ADD_TASK':
            console.log('ADD_TASK', state)
            return  ({...state,
                //   id: action.id,
                  text: text.push(action.text),
                })
        default:
            console.log('   DEFAULT REDUCER', state)
            return state
    }
}
