import { connect } from 'react-redux';
import { addTask } from './ActionCreator.js'

const mapStateToProps = state => {
    console.log("state", state)
    return {
        tasks: state.text
    };
}
class ToDoApp extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        inputValue: ''
      };
    }

    handleChange = e => {
        this.setState({
            inputValue: e.target.value,
        })
    }
    handleNewTask = () => {
        this.props.dispatch(addTask({
            text: this.state.inputValue
        }))
    }

    handleDeleteTask = () => {

    }

    handleEditTask = () => {

    }

    render(){
        console.log(this.props.tasks)

        return(
            <>
                <h3>To-Do App</h3>
                <input type = 'text' value = {this.state.inputValue} placeholder = "Enter new task" onChange={this.handleChange}/>
                <button onClick = {this.handleNewTask}>Add New Task</button>
                {
                    this.props.task.map((task, index) => {
                        <li key = {index}>
                            {task} 
                            <button onClick = {this.handleEditTask}>Edit Task</button>
                            <button onClick = {this.handleDeleteTask}>Delete Task</button>
                        </li>
                    })
                }
            </>
        )
    }
}
export default connect(mapStateToProps)(ToDoApp);
