let newId = 0;
export const Reducer = (state = [], action) => {
    console.log("reducer", action)
    switch (action.type) {
        case 'ADD_TASK':
            return [ ...state,
                {
                  id: newId++,
                  text: action.text,
                }
            ]
        case 'DELETE_TASK':
            console.log('DELETE_TASK', state)
            return [ ...state,
                {
                    id: newId++,
                    text: action.text,
                }
            ]
        default:
            console.log('   DEFAULT REDUCER', state)
            return state
    }
}
