import React from 'react';
import { connect } from 'react-redux';
import { addTask, deleteTask, editTask} from './ActionCreator.js'

const mapStateToProps = state => {
    console.log("state", state)
    return {
        tasks: state
    };
}
class ToDoApp extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        inputValue: ''
      };
    }

    handleChange = e => {
        this.setState({
            inputValue: e.target.value,
        })
    }
    handleNewTask = () => {
        this.props.dispatch(addTask(
           this.state.inputValue
        ))
        this.setState({
            inputValue: '',
        })
    }

    handleDeleteTask = (id) => {
        this.props.dispatch(deleteTask(id))
    }

    handleEditTask = (id, text) => {
        console.log(id)
        document.getElementById('inputTask').focus();
        document.getElementById('inputTask').value = text;
        this.props.dispatch(editTask(
            this.state.inputValue,
            id
         ))
         this.setState({
             inputValue: '',
         })
    }

    render(){
        console.log(this.props.tasks)

        return(
            <>
                <h3>To-Do App</h3>
                <input id = "inputTask"type = 'text' value = {this.state.inputValue} placeholder = "Enter new task" onChange={this.handleChange}/>
                <button onClick = {this.handleNewTask}>Save</button>
                <ul>
                {
                    this.props.tasks && this.props.tasks.map((task, index) => {
                        console.log(task)
                       return  <li key = {index}>
                                {task.text} 
                                <button onClick = {()=>this.handleEditTask(task.id, task.text)}>Edit Task</button>
                                <button onClick = {()=>this.handleDeleteTask(task.id)}>Delete Task</button>
                            </li>
                    })
                }
                </ul>
            </>
        )
    }
}
export default connect(mapStateToProps)(ToDoApp);
