let newId = 0;
export const Reducer = (state = [], action) => {
    console.log("reducer", action)
    switch (action.type) {
        case 'ADD_TASK':
            return [ ...state,
                {
                  id: newId++,
                  text: action.text,
                }
            ]
        case 'DELETE_TASK':
            return state.filter(task => task.id !== action.id)

        case 'EDIT_TASK':
            console.log('EDIT_TASK', typeof(action.id))
            return [state.map(task => {
                    if(task.id === action.id){
                        task.text = action.text
                    }
                })
            ]

        default:
            return state
    }
}
